 CREATE TABLE usager (
	username varchar(20),
	nom varchar(50) NOT NULL,
	prenom varchar(50) NOT NULL,
    password varchar(32) NOT NULL,
	PRIMARY KEY(username)
);

CREATE TABLE article (
	idArticle SMALLINT NOT NULL AUTO_INCREMENT,
	titre_anglais varchar(500) NOT NULL,
	titre_francais varchar(500) NOT NULL,
	texte_anglais TEXT NOT NULL,
	texte_francais TEXT NOT NULL,
    auteur varchar(20) NOT NULL,
	PRIMARY KEY(idArticle),
	FOREIGN KEY (auteur) references usager(username) 
    
);


CREATE TABLE motscles (
    idMotscles SMALLINT NOT NULL AUTO_INCREMENT,
    mots_cles varchar (100) NOT NULL,
    PRIMARY KEY (idMotscles)
);


CREATE TABLE article_motscles( 
	articleId SMALLINT NOT NULL,
    motsclesId SMALLINT NOT NULL,
	FOREIGN KEY(articleId) REFERENCES article(idArticle),
    FOREIGN kEY (motsclesId) references motscles(idMotscles)   
);


